mapper.update:
		UPDATE AF_FLOW

		
			
				status=#{status}
			
			
				,updateTime=#{updateTime}
			
			
				, finishTime=#{finishTime}
			
		

		WHERE id = #{id}

mapper.select:
SELECT

FROM AF_FLOW

	
		and id=#{id}
	
	
		and name=#{name}
	
	
		and processId=#{processId}
	
	
		and status=#{status}
	
	
		and variables=#{variables}
	
	
		and createTime like #{createTime}

mapper.update:
UPDATE AF_FLOW SET
settleDate=#{settleDate}, busiLine=#{busiLine}, province=#{province}
WHERE id=#{id}
