mapper.update:
		UPDATE AF_FLOW

		
			
				status=#{status}
			
			
				,updateTime=#{updateTime}
			
			
				, finishTime=#{finishTime}
			
		

		WHERE id = #{id}

mapper.select:
SELECT

FROM AF_FLOW

	
		and id=#{id}
	
	
		and name=#{name}
	
	
		and processId=#{processId}
	
	
		and processName=#{processName}
	
	
		and status=#{status}
	
	
		and variables=#{variables}
	
	
		and settleDate=#{settleDate}
	
	
		and busiLine=#{busiLine}
	
	
		and province=#{province}
